document,new class{constructor(){this.ajaxUrl=siteConfig?.ajaxUrl??"",this.ajaxNonce=siteConfig?.ajax_nonce??"",this.loadMoreBtnPrev=$("#tops_button_left"),this.loadMoreBtnNext=$("#tops_button_right"),this.init()}init(){(this.loadMoreBtnPrev.length||this.loadMoreBtnNext.length)&&(this.loadMoreBtnPrev.on("click",()=>this.handleReviewLoadMorePostsPrev()),this.loadMoreBtnNext.on("click",()=>this.handleReviewLoadMorePostsNext()))}handleReviewLoadMorePostsPrev(){const e=this.loadMoreBtnPrev.data("page");if(!e)return null;const o=parseInt(e)-1;$.ajax({url:this.ajaxUrl,type:"post",data:{page:o,action:"tops_load_more",ajax_nonce:this.ajaxNonce},success:t=>{0!==parseInt(t)&&($(".tops_slider").empty(),$(".tops_slider").append(t),this.loadMoreBtnNext.data("page",e),this.loadMoreBtnPrev.data("page",o))},error:t=>{console.log(t)}})}handleReviewLoadMorePostsNext(){const e=this.loadMoreBtnNext.data("page");if(!e)return null;const o=parseInt(e)+1;$.ajax({url:this.ajaxUrl,type:"post",data:{page:e,action:"tops_load_more",ajax_nonce:this.ajaxNonce},success:t=>{0===parseInt(t)?console.log("Data not found"):($(".tops_slider").empty(),$(".tops_slider").append(t),this.loadMoreBtnPrev.data("page",e),this.loadMoreBtnNext.data("page",o))},error:t=>{console.log(t)}})}};